import React from 'react'
import { Table ,Button } from 'semantic-ui-react'
import { useState ,useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { useHistory } from 'react-router-dom';



export const Read = () => {

//let history = useHistory();
const [APIData , setAPIData] = useState([]);
  useEffect(() => {
    axios.get(`https://64e302a0bac46e480e780350.mockapi.io/fakeData`)
        .then((response) => {
            setAPIData(response.data);
        })
}, [])

const setData = (data) => {
    let { id, firstName, lastName, checkbox } = data;
    localStorage.setItem('ID', id);
    localStorage.setItem('First Name', firstName);
    localStorage.setItem('Last Name', lastName);
    localStorage.setItem('Checkbox Value', checkbox)
}

const getData = () => {
    axios.get(`https://64e302a0bac46e480e780350.mockapi.io/fakeData`)
        .then((getData) => {
             setAPIData(getData.data);
         })
}

const onDelete = (id) => {
    const result = window.confirm('Are you sure you want to delete this data?');
    if (!result) {
      alert('Your Data is safe!');
    } else {
      axios.delete(`https://64e302a0bac46e480e780350.mockapi.io/fakeData/${id}`);
      alert('Data is deleted!');
      window.location.reload();
    }
  };


// const onDelete = (id) => {
//     axios.delete(`https://64e302a0bac46e480e780350.mockapi.io/fakeData/${id}`)
//  .then(() => {
//     getData();
// })
// }

  return (
    <div>
            <Table singleLine>
                <Table.Header>
                    <Table.Row>
                        <Table.HeaderCell>First Name</Table.HeaderCell>
                        <Table.HeaderCell>Last Name</Table.HeaderCell>
                        <Table.HeaderCell>Checked</Table.HeaderCell>
                        <Table.HeaderCell>Update</Table.HeaderCell>
                        <Table.HeaderCell>Delete</Table.HeaderCell>
                    </Table.Row>
                </Table.Header>

                <Table.Body>
                {APIData.map((data) => {
                    return (
                    <Table.Row>
                        <Table.Cell>{data.firstName}</Table.Cell>
                        <Table.Cell>{data.lastName}</Table.Cell>
                        <Table.Cell>{data.checkbox ? 'Checked' : 'Unchecked'}</Table.Cell>
                        <Table.Cell> <Link to="/update"> <Button onClick={() => setData(data)}>Update</Button></Link></Table.Cell>
                        <Table.Cell>
                        <Link to="/crud"> <Button onClick={() => onDelete(data.id)}>Delete</Button></Link> 

                        {/* <Button onClick={() => onDelete(data.id)}>Delete</Button> */}
                        </Table.Cell>
                        </Table.Row>
                )})}
                </Table.Body>
            </Table>
        </div>
  )
}
