import React, { useEffect, useState } from "react";

export const Home = () => {
  const textArray = ["hard", "fun", "a journey", "LIFE"];
  const typingDelay = 2000;
  const erasingDelay = 1000;
  const newTextDelay = 20000;

  const [textIndex, setTextIndex] = useState(0);
  const [charIndex, setCharIndex] = useState(0);
  const [isTyping, setIsTyping] = useState(true);

  useEffect(() => {
    const typedTextSpan = document.querySelector(".typed-text");
    const cursorSpan = document.querySelector(".cursor");

    if (isTyping) {
      if (charIndex < textArray[textIndex].length) {
        if (!cursorSpan.classList.contains("typing")) {
          cursorSpan.classList.add("typing");
        }
        typedTextSpan.textContent += textArray[textIndex].charAt(charIndex);
        setCharIndex(charIndex + 1);
        setTimeout(() => setIsTyping(false), typingDelay);
      } else {
        cursorSpan.classList.remove("typing");
        setTimeout(() => setIsTyping(false), newTextDelay);
      }
    } else {
      if (charIndex > 0) {
        if (!cursorSpan.classList.contains("typing")) {
          cursorSpan.classList.add("typing");
        }
        const newText = textArray[textIndex].substring(0, charIndex - 1);
        typedTextSpan.textContent = newText;
        setCharIndex(charIndex - 1);
        setTimeout(() => setIsTyping(true), erasingDelay);
      } else {
        cursorSpan.classList.remove("typing");
        setTextIndex((prevIndex) =>
          prevIndex === textArray.length - 1 ? 0 : prevIndex + 1
        );
        setTimeout(() => setIsTyping(true), typingDelay);
      }
    }
  }, [charIndex, isTyping, textArray, textIndex]);

  return (
    <div>
      <br />
      <h1>This is the content Present in the Home Component.</h1>
      <div className="container">
        <p>
          Coding is <span className="typed-text"></span>
          <span className="cursor">&nbsp;</span>
        </p>
      </div>
    </div>
  );
};
