import React, { useEffect, useState } from "react";

export const Home = () => {
  const wordsArray = ["Hello", "World", "React", "Typing", "Effect"];
  const [wordIndex, setWordIndex] = useState(0);
  const [charIndex, setCharIndex] = useState(0);
  const [currentWord, setCurrentWord] = useState("");
  const typingDelay = 200;
  const erasingDelay = 100;
  const newTextDelay = 1000;

  useEffect(() => {
    const typingInterval = setInterval(() => {
      if (charIndex < wordsArray[wordIndex].length) {
        if (!currentWord.endsWith(" ")) {
          setCurrentWord(currentWord + wordsArray[wordIndex].charAt(charIndex));
          setCharIndex(charIndex + 1);
        } else {
          setCurrentWord(wordsArray[wordIndex].substring(0, charIndex + 1));
          setCharIndex(charIndex + 1);
        }
      } else {
        clearInterval(typingInterval);
        setTimeout(erase, newTextDelay);
      }
    }, typingDelay);

    const erase = () => {
      const erasingInterval = setInterval(() => {
        if (charIndex >= 0) {
          setCurrentWord(currentWord.substring(0, charIndex));
          setCharIndex(charIndex - 1);
        } else {
          clearInterval(erasingInterval);
          setWordIndex((prevIndex) => (prevIndex + 1) % wordsArray.length);
          setTimeout(() => {
            setCharIndex(0);
            setCurrentWord("");
          }, typingDelay);
        }
      }, erasingDelay);
    };

    return () => clearInterval(typingInterval);
  }, [charIndex, wordIndex, currentWord, wordsArray]);

  return (
    <div>
      <br />
      <h1>Typing Effect:</h1>
      <h4>This is the typing effect {currentWord}</h4> <br />
    </div>
  );
};
