import React, { useEffect, useState } from "react";

export const Home = () => {
  const [textArray] = useState(["hard", "fun", "a journey", "LIFE"]);
  const [textArrayIndex, setTextArrayIndex] = useState(0);
  const [charIndex, setCharIndex] = useState(0);
  const [typing, setTyping] = useState(true);

  useEffect(() => {
    const typedTextSpan = document.querySelector(".typed-text");
    const cursorSpan = document.querySelector(".cursor");

    const typingDelay = 200;
    const erasingDelay = 100;
    const newTextDelay = 2000; // Delay between current and next text

    function type() {
      if (charIndex < textArray[textArrayIndex].length) {
        if (!cursorSpan.classList.contains("typing")) {
          cursorSpan.classList.add("typing");
        }
        typedTextSpan.textContent +=
          textArray[textArrayIndex].charAt(charIndex);
        setCharIndex(charIndex + 1);
        setTimeout(type, typingDelay);
      } else {
        cursorSpan.classList.remove("typing");
        setTimeout(erase, newTextDelay);
      }
    }

    function erase() {
      if (charIndex > 0) {
        if (!cursorSpan.classList.contains("typing")) {
          cursorSpan.classList.add("typing");
        }
        typedTextSpan.textContent = textArray[textArrayIndex].substring(
          0,
          charIndex - 1
        );
        setCharIndex(charIndex - 1);
        setTimeout(erase, erasingDelay);
      } else {
        cursorSpan.classList.remove("typing");
        setTextArrayIndex((prevIndex) =>
          prevIndex === textArray.length - 1 ? 0 : prevIndex + 1
        );
        setTimeout(() => {
          setTyping(true);
          type();
        }, typingDelay + 1100);
      }
    }

    if (typing) {
      type();
      setTyping(false);
    }
  }, [charIndex, textArray, textArrayIndex, typing]);

  return (
    <div>
      <br />
      <h1>This is the content Present in the Home Component.</h1>
      <div className="container">
        <p>
          Coding is <span className="typed-text"></span>
          <span className="cursor">&nbsp;</span>
        </p>
      </div>
    </div>
  );
};
