import React from 'react'
import { useState } from 'react'



export const Count = () => {

    const [num,setNum] = useState(0);

      const plus = () => {
        setNum(num + 1);
      }

      const sub = () => {
        setNum(num - 1);
      }

      const reset = () => {
        setNum(0);
      }

      const [color, setColor] = useState('red');

    const colorChange = () => {
        const selectedOption = document.querySelector('#myDropdown .dropdown-content p.active');
        if (selectedOption) {
            setColor(selectedOption.textContent);
        }
    };

  return (
    <div> <br />
        <h2>This is the Count component</h2>

        <div class='box' style={{backgroundColor: color}}></div> <br />
        <button onClick={colorChange}>color change</button>

        <div class="dropdown">
        <button onclick="myFunction()" class="dropbtn">Dropdown</button>
            <div id="myDropdown" class="dropdown-content">
                <a href="#home">Home</a>
                <a href="#about">About</a>
                <a href="#contact">Contact</a>
            </div>
        </div>

        <h1>{num}</h1>
        <button onClick={plus}>+</button> &nbsp;
        <button onClick={sub}>-</button> &nbsp;
        <button onClick={reset}>reset</button> <br /><br />
    </div>
  )
}
