import React from 'react'
import { useState } from 'react'



export const Count = () => {

    const [num,setNum] = useState(0);

      const plus = () => {
        setNum(num + 1);
      }

      const sub = () => {
        setNum(num - 1);
      }

      const reset = () => {
        setNum(0);
      }


      const [isShown, setIsShown] = useState(false);
      const handleClick = event => {
        setIsShown(current => !current);
        // if (isShown) {
        //     hide();

        // }
      }

      const [color, setColor] = useState('red');

      const colorChange = () => {
        const selectedOption = document.querySelector('#myDropdown .dropdown-content p.active');
        if (selectedOption) {
          setColor(() => selectedOption.textContent);
        }
      };

  return (
    <div> <br />
        <h2>This is the Count component</h2>

        <div class='box' style={{backgroundColor: color}}></div> <br />

        <div class="dropdown">
        <button onclick={handleClick} className="dropbtn">Color Change</button>
            <div id="myDropdown" className="dropdown-content">
                <p onClick={() => setColor('red')}>red</p>
                <p onClick={() => setColor('green')}>green</p>
                <p onClick={() => setColor('blue')}>blue</p>
            </div>
        </div>

        <h1>{num}</h1>
        <button onClick={plus}>+</button> &nbsp;
        <button onClick={sub}>-</button> &nbsp;
        <button onClick={reset}>reset</button> <br /><br />
    </div>
  )
}
