import React from 'react'
import { useState } from 'react'



export const Count = () => {

    const [num,setNum] = useState(0);

      const plus = () => {
        setNum(num + 1);
      }

      const sub = () => {
        setNum(num - 1);
      }

      const reset = () => {
        setNum(0);
      }


      const [isShown, setIsShown] = useState(false);
      const handleClick = event => {
        setIsShown(current => !current);
        // if (isShown) {
        //     hide();

        // }
      }

      const [color, setColor] = useState('red');

      const colorChange1 = () => {
        const selectedOption = document.querySelector('#myDropdown .dropdown-content p.active');
        if (selectedOption) {
          setColor(() => selectedOption.textContent);
        }
      };

      const [resultsPerPage, setResultsPerPage] = React.useState('black');

      const colorChange = (event) => {
        const selectedOption = event.target.value;
        setResultsPerPage(selectedOption);
        document.querySelector('.box').style.backgroundColor = selectedOption;
      };

  return (
    <div> <br />
        <h2>This is the Count component</h2>

        <div class='box' style={{backgroundColor: color}}></div> <br />

    <br /><br />

        <div>
            <select value={resultsPerPage} onChange={colorChange}>
                <option value={'red'}>Red</option>
                <option value={'green'}>Green</option>
                <option value={'blue'}>Blue</option>
            </select>
            <p>Results per page: {resultsPerPage}</p>
        </div>

        {/* <div>
            <select value={resultsPerPage} onChange={(event) => setResultsPerPage(event.target.value)}>
                <option value={'red'}>Red</option>
                <option value={'green'}>Green</option>
                <option value={'blue'}>Blue</option>
            </select>
            <p>Results per page: {resultsPerPage}</p>
        </div> */}


        <h1>{num}</h1>
        <button onClick={plus}>+</button> &nbsp;
        <button onClick={sub}>-</button> &nbsp;
        <button onClick={reset}>reset</button> <br /><br />
    </div>
  )
}
