import React, { useEffect, useState } from "react";

export const Home = () => {
  const wordsArray = ["Hello", "World", "React", "Typing", "Effect"];
  const [wordIndex, setWordIndex] = useState(0);
  const [charIndex, setCharIndex] = useState(0);
  const [currentWord, setCurrentWord] = useState("");
  const [isDeleting, setIsDeleting] = useState(false);
  const typingDelay = 100; // Delay between typing each character
  const newTextDelay = 1000; // Delay before typing the next word

  useEffect(() => {
    const type = () => {
      if (charIndex < wordsArray[wordIndex].length) {
        setCurrentWord((prev) => prev + wordsArray[wordIndex][charIndex]);
        setCharIndex((prev) => prev + 1);
      } else if (charIndex === wordsArray[wordIndex].length) {
        setIsDeleting(true);
      }
    };

    const erase = () => {
      if (charIndex > 0) {
        setCurrentWord((prev) => prev.slice(0, prev.length - 1));
        setCharIndex((prev) => prev - 1);
      } else if (charIndex === 0) {
        setIsDeleting(false);
        setWordIndex((prev) => (prev + 1) % wordsArray.length);
      }
    };

    const typingInterval = setInterval(() => {
      if (isDeleting) {
        erase();
      } else {
        type();
      }
    }, typingDelay);

    return () => clearInterval(typingInterval);
  }, [charIndex, wordIndex, isDeleting]);

  return (
    <div>
      <br />
      <h1>Typing Effect:</h1>
      <h4>This is the typing effect {currentWord}</h4> <br />
    </div>
  );
};
